import {createAsyncThunk, createSlice, PayloadAction} from "@reduxjs/toolkit"
import {fetchAllNotes, createNote} from "../services/api";

export type NoteType = {
  id: any
  title: string
  category: string
  content: string
  created: string
  dates: string
  archived: boolean
}

export type NoteEditType = {
  id: any
  title: string
  category: string
  content: string
  dates: string
}

type NotesStateType = {
  notes: NoteType[]
}

const initialState: NotesStateType = {
  notes: []
}

export const fetchNotes = createAsyncThunk(
  "notes/fetchNotes",
  async () => {
    return await fetchAllNotes()
  }
);

export const createNewNote = createAsyncThunk(
  "notes/createNewNote",
  async (noteData: NoteType) => {
    try{
      const response = await createNote(noteData);
      console.log('createNewNote', response)
      return response;
    } catch (e) {
      console.log('createNewNote', e)
    }
  }
);

const notesSlice = createSlice({
  name: 'notes',
  initialState,
  reducers: {
    editNote: (state, action: PayloadAction<NoteEditType>) => {
      const {id, title, category, content, dates} = action.payload
      const noteToEdit = state.notes.find((note) => note.id === id)
      if (noteToEdit) {
        noteToEdit.title = title
        noteToEdit.category = category
        noteToEdit.content = content
        noteToEdit.dates = dates
      }
    },
    toggleArchive: (state, action: PayloadAction<number>) => {
      const id = action.payload
      const toggledNote = state.notes.find(note => note.id === id)
      if (toggledNote) {
        toggledNote.archived = !toggledNote.archived
      }
    },
    removeNote: (state, action: PayloadAction<number>) => {
      state.notes = state.notes.filter((note) => note.id !== action.payload)
    }
  },
  extraReducers: (builder) => {
    builder.addCase(createNewNote.fulfilled, (state, action) => {
      state.notes.push(action.payload);
      console.log('action.payload', action.payload)
    })
    builder.addCase(fetchNotes.fulfilled, (state, action) => {
      state.notes = action.payload.flat();
    })

  },
})

export const {
  removeNote,
  editNote,
  toggleArchive
} = notesSlice.actions


export default notesSlice.reducer
